import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CrudService } from './../../service/crud.service';

@Component({
  selector: 'app-edit-book',
  templateUrl: './edit-book.component.html',
  styleUrls: ['./edit-book.component.css']
})
export class EditBookComponent implements OnInit {
  book: any = {}; // Initialize with an empty book object

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private crudService: CrudService
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.params['id']; // Updated line
    this.crudService.GetBookById(id).subscribe((res: any) => {
      this.book = res;
    });
  }

  onSubmit(): void {
    this.crudService.EditBook(this.book._id, this.book).subscribe((res: any) => {
      console.log('Book updated successfully:', res);
      this.router.navigate(['/books-list']); // Navigate back to the book list after updating
    });
  }

}
<form (submit)="onSubmit()">
    <div class="form-group">
      <label for="isbn">ISBN:</label>
      <input type="text" class="form-control" id="isbn" [(ngModel)]="book.isbn" name="isbn">
    </div>

    <div class="form-group">
      <label for="title">Title:</label>
      <input type="text" class="form-control" id="title" [(ngModel)]="book.title" name="title">
    </div>

    <div class="form-group">
      <label for="author">Author:</label>
      <input type="text" class="form-control" id="author" [(ngModel)]="book.author" name="author">
    </div>

    <div class="form-group">
      <label for="description">Description:</label>
      <textarea class="form-control" id="description" [(ngModel)]="book.description" name="description"></textarea>
    </div>

    <div class="form-group">
      <label for="publishedYear">Published Year:</label>
      <input type="text" class="form-control" id="publishedYear" [(ngModel)]="book.published_year" name="published_year">
    </div>

    <div class="form-group">
      <label for="publisher">Publisher:</label>
      <input type="text" class="form-control" id="publisher" [(ngModel)]="book.publisher" name="publisher">
    </div>

    <button type="submit" class="btn btn-primary">Save Changes</button>
  </form>
