import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CrudService } from './../../service/crud.service';

@Component({
  selector: 'app-books-list',
  templateUrl: './books-list.component.html',
  styleUrls: ['./books-list.component.css']
})
export class BooksListComponent implements OnInit {
  Books: any = [];

  constructor(private crudService: CrudService, private router: Router) {}

  ngOnInit(): void {
    this.loadBooks();
  }

  loadBooks(): void {
    this.crudService.GetBooks().subscribe((res: any) => {
      console.log(res);
      this.Books = res;
    });
  }

  onEdit(id: any): void {
    this.router.navigate(['/edit-book', id]);
  }

  onDelete(id: any): void {
    this.crudService.DeleteBook(id).subscribe((res: any) => {
      console.log('Book deleted successfully:', res);
      this.loadBooks();
    });
  }
}
<div class="container">
  <div class="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
    <h2 class="h2">Books List</h2>
  </div>

  <div class="table-responsive">
    <table class="table table-bordered">
      <thead>
        <tr>
          <th scope="col">Id</th>
          <th scope="col">ISBN</th>
          <th scope="col">Title</th>
          <th scope="col">Author</th>
          <th scope="col">Description</th>
          <th scope="col">Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let book of Books; let i = index">
          <th scope="row">{{book._id}}</th>
          <td>{{book.isbn}}</td>
          <td>{{book.title}}</td>
          <td>{{book.author}}</td>
          <td>{{book.description}}</td>
          <td class="text-center">
            <button class="btn btn-sm btn-primary" [routerLink]="['/edit-book', book._id]">Edit</button>
            <button class="btn btn-sm btn-danger" (click)="onDelete(book._id)">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
